// Extract search parameters from input
const input = $input.first().json;

const searchParams = {
  query: input.query || 'physiotherapy clinic near New York',
  niche: input.niche || 'physiotherapy clinic',
  location: input.location || 'New York, NY',
  maxResults: input.maxResults || 20
};

// Prepare Google Maps search URL
// Note: In production, use official Google Places API
const encodedQuery = encodeURIComponent(searchParams.query);

return [{
  json: {
    ...searchParams,
    searchUrl: `https://www.google.com/maps/search/${encodedQuery}`,
    placesApiUrl: `https://maps.googleapis.com/maps/api/place/textsearch/json`,
    timestamp: new Date().toISOString()
  }
}];

// Normalize and combine results from different sources
const items = $input.all();
const normalizedLeads = [];

for (const item of items) {
  const data = item.json;

  // Handle Google Places API results
  if (data.results && Array.isArray(data.results)) {
    for (const place of data.results) {
      normalizedLeads.push({
        businessName: place.name || '',
        address: place.formatted_address || '',
        phone: '', // Requires additional API call
        website: '', // Requires additional API call
        placeId: place.place_id || '',
        rating: place.rating || 0,
        reviewCount: place.user_ratings_total || 0,
        source: 'google_places',
        rawData: place
      });
    }
  }

  // Handle Yelp API results
  if (data.businesses && Array.isArray(data.businesses)) {
    for (const biz of data.businesses) {
      normalizedLeads.push({
        businessName: biz.name || '',
        address: biz.location ? 
          `${biz.location.address1 || ''}, ${biz.location.city || ''}, ${biz.location.state || ''} ${biz.location.zip_code || ''}` : '',
        phone: biz.phone || biz.display_phone || '',
        website: biz.url || '',
        yelpId: biz.id || '',
        rating: biz.rating || 0,
        reviewCount: biz.review_count || 0,
        categories: (biz.categories || []).map(c => c.title).join(', '),
        source: 'yelp',
        rawData: biz
      });
    }
  }

  // Handle SerpAPI Google Maps results
  if (data.local_results && Array.isArray(data.local_results)) {
    for (const result of data.local_results) {
      normalizedLeads.push({
        businessName: result.title || '',
        address: result.address || '',
        phone: result.phone || '',
        website: result.website || '',
        placeId: result.place_id || '',
        rating: result.rating || 0,
        reviewCount: result.reviews || 0,
        source: 'serpapi_maps',
        rawData: result
      });
    }
  }
}

// Return normalized leads
return normalizedLeads.map(lead => ({ json: lead }));

// Add search metadata to each lead
const items = $input.all();
const inputData = $('Prepare Search Query').first().json;

return items.map(item => ({
  json: {
    ...item.json,
    searchNiche: inputData.niche,
    searchLocation: inputData.location,
    searchQuery: inputData.query,
    discoveredAt: new Date().toISOString()
  }
}));
